document.addEventListener("DOMContentLoaded",function(){$(function(){$(".slider").each(function(){var slider=$(this),slides=slider.find(".slide"),totalSlides=slides.length,currIndex=0,imgCache=[],intervalTime=5000,sliderInterval;function cycleItems(){var currSlide=slides.eq(currIndex);slides.fadeOut(500).css("z-index",1);currSlide.fadeIn(500).css("z-index",5)}function changeSlide(){currIndex+=1;if(currIndex>totalSlides-1){currIndex=0}cycleItems()}function startSlider(){clearInterval(sliderInterval);sliderInterval=setInterval(function(){changeSlide()},intervalTime)}(function preloader(){if(currIndex<totalSlides){imgCache[currIndex]=new Image();imgCache[currIndex].src=slides.eq(currIndex).find("img").attr("src");imgCache[currIndex].onload=function(){currIndex+=1;preloader()}}else{currIndex=0;cycleItems();startSlider()}}());$(".next-slide").on("click",function(){currIndex+=1;if(currIndex>totalSlides-1){currIndex=0}cycleItems();startSlider(intervalTime=10000)});$(".prev-slide").on("click",function(){currIndex-=1;if(currIndex<0){currIndex=totalSlides-1}cycleItems();startSlider(intervalTime=10000)});});});(function(){const getToggle=document.querySelectorAll(".js-toggle");const getToggleReset=document.querySelectorAll(".js-toggle-reset");for(let i=0;i<getToggle.length;i+=1){getToggle[i].addEventListener("click",function(e){if(this.classList.contains("active")===false){this.classList.add("active")}else if(this.classList.contains("active")===true){this.classList.remove("active")}e.stopPropagation()})}document.addEventListener("click",function(e){if(e.target!==getToggleReset){for(let i=0;i<getToggleReset.length;i+=1){getToggleReset[i].classList.remove("active")}}})})();(function(){const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i+=1){intLinks[i].addEventListener("click",function(e){e.preventDefault();document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}})();});
